import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function NewNote() {
  const [note, setNote] = useState({ title: "", content: "" });
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    await axios.post("http://localhost:4000/api/notes", note, {
      headers: { Authorization: `Bearer ${token}` }
    });
    navigate("/dashboard");
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>New Note</h2>
      <input type="text" placeholder="Title" onChange={e => setNote({ ...note, title: e.target.value })} required />
      <textarea placeholder="Content" onChange={e => setNote({ ...note, content: e.target.value })} required />
      <button type="submit">Save</button>
    </form>
  );
}
