import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

export default function EditNote() {
  const [note, setNote] = useState({ title: "", content: "" });
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNote = async () => {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:4000/api/notes", {
        headers: { Authorization: `Bearer ${token}` }
      });
      const noteData = res.data.find(n => n._id === id);
      setNote(noteData);
    };
    fetchNote();
  }, [id]);

  const handleSubmit = async e => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    await axios.put(`http://localhost:4000/api/notes/${id}`, note, {
      headers: { Authorization: `Bearer ${token}` }
    });
    navigate("/dashboard");
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Edit Note</h2>
      <input type="text" value={note.title} onChange={e => setNote({ ...note, title: e.target.value })} required />
      <textarea value={note.content} onChange={e => setNote({ ...note, content: e.target.value })} required />
      <button type="submit">Update</button>
    </form>
  );
}
